export class Masa implements isConvertible<{unitname:string, val:number}>{
    unidades: [string[], number[]];
    constructor(){
        this.unidades = [["kg", "g"], [1, 1000]];
    }
    convert(val1:{unitname:string, val:number}, val2:string): {unitname:string, val:number}{
